import React, { Component } from "react";
import "./CityWeather.css";
import axios from "axios";
import { thisExpression } from "@babel/types";

export default class CityWeather extends Component {
  state = {
    loaded: false,
    unit: "metrics"
  };
  label() {
    if (this.props.temp < 0)
      return <div className="badge badge-primary"> FREEZING </div>;
    if (this.props.temp > 25)
      return <div className="badge badge-danger"> HOT </div>;
    if (this.props.temp < 10)
      return <div className="badge badge-info"> COLD </div>;
    return <div className="badge badge-warning"> WARM </div>;
  }

  convertTemp = () => {
    alert("Converting temp");
  };

  temperature = () => {
    if (this.state.loaded) {
      return (
        <p className="card-text city-temperature">
          {this.state.temp}
          <small>
            °C |{" "}
            <span className="fahrenheit" onClick={this.convertTemp}>
              °F
            </span>
          </small>
        </p>
      );
    } else {
      this.setState({
        loaded: true,
        temp: 19,
        units: "metrics"
      });
      url = "openweather....q=lisbon"

      function getUv(lattitude, longitude) {
        coordUrl = "openweather....lat=lattitude&sadkasd"
        axios.get(coordUrl).then(displayUV)
      }

      function getCoordinates(response) {
        let lat = reponse.data.coords.lat;
        let lon = reponse.data.coords.lon;

        getUv(lat, lon);
      }

      axios.get(url).then(getCoordinates);

    }
  };

  render() {
    return (
      <div className="card">
        <div className="card-body bg-light">
          {this.label()}
          <h5 className="card-title">
            <strong> {this.props.name} </strong>
          </h5>
          {this.temperature()}
          <a
            href={`https://www.google.com/search?q=weather+${this.props.name}`}
            target="_blank"
            className="btn btn-primary btn-sm shadow-sm"
          >
            View forecast
          </a>
        </div>
      </div>
    );
  }
}
